import React, { Component } from "react"
import { makeGetRequest } from "../../api_calls"

class GiveCode extends Component {
    constructor(props) {
        super(props)
        this.state = {
            code: 'funky chicken'
        }
    }

    generateCode = () => {
        makeGetRequest('business/generateCode',
            (code) => {
                console.log(code)
                this.setState({code})
            }
        )
    }

    render() {
        return (
            <div style={{height: '100%', display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'space-around'}}>
                {/* <div className="panel"> */}
                    <div style={{padding: '50px', fontSize: '70px', fontFamily: 'var(--font1)'}}>{this.state.code}</div>
                    <button style={{width: '300px', margin: '0 auto', padding: '20px', fontSize: '18px'}} onClick={this.generateCode}>Click to generate</button>
                {/* </div> */}
            </div>
        )
    }
}

export default GiveCode
