import React, { Component } from "react"

class AddOptions extends Component {
    constructor(props) {
        super(props)
        this.state = {
            new_key: '',
            new_val: ''
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        },
            () => {console.log(this.state)}
        )
    }

    addOption = () => {
        this.props.addOption(this.state.new_key, this.state.new_val)
    }

    render() {
        return (
            <>
                <input className="text-response" value={this.state.new_key} onChange={this.handleChange} />
                <input className="text-response" value={this.state.new_val} onChange={this.handleChange} />
            </>
        )
    }
}

export default AddOptions
