import React, { Component } from "react"

class AddOptions extends Component {
    constructor(props) {
        super(props)
        this.state = {
            new_key: '',
            new_val: ''
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        },
            () => {console.log(this.state)}
        )
    }

    addOption = () => {
        this.props.addOption(this.state.new_key, this.state.new_val)
    }

    render() {
        return (
            <>
            <div className="field-title">
                {this.props.title}
            </div>
            <div style={{display: 'flex', marginTop: '10px'}}>
                <div>
                    <div className="field-title">
                        {this.props.key_name}
                    </div>
                    <input className="text-response" value={this.state.new_key} onChange={this.handleChange} style={{width: '30%',marginRight: '20px'}}/>
                </div>
                <div>
                    <div className="field-title">
                        {this.props.val_name}
                    </div>
                    <input className="text-response" value={this.state.new_val} onChange={this.handleChange} />
                </div>
            </div>
            </>
        )
    }
}

export default AddOptions
