import React, { Component } from "react"
import tick from "../../images/icons/tick.png"

class AddOptions extends Component {
    constructor(props) {
        super(props)
        this.state = {
            new_key: '',
            new_val: ''
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        },
            () => {console.log(this.state)}
        )
    }

    addOption = () => {
        this.props.addOption(this.state.new_key, this.state.new_val)
    }

    render() {
        return (
            <>
            <div className="field-title">
                {this.props.title}
            </div>
            <div style={{display: 'flex', marginTop: '10px'}}>
                {
                    Object.entries(this.props.options).map(
                        (k, v) => {
                            console.log(k, v)
                        }
                    )
                }
                <div>
                    <div className="field-title" style={{fontSize: '10px'}}>
                        {this.props.key_name}
                    </div>
                    <input id="key" className="text-response" value={this.state.new_key} onChange={this.handleChange} style={{width: '50%'}}/>
                </div>
                <div>
                    <div className="field-title" style={{fontSize: '10px'}}>
                        {this.props.val_name}
                    </div>
                    <input id="val" className="text-response" value={this.state.new_val} onChange={this.handleChange} />
                </div>
                <button style={{margin: '10px'}} onClick={this.addOption}>
                    <img src={tick} style={{height: '10px'}}/>
                </button>
            </div>
            </>
        )
    }
}

export default AddOptions
