import React, { Component } from "react"
import { makePostRequest } from "../../api_calls"

class EnterInstagram extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: ''
        }
    }

    enterInstagram = () => {
        makePostRequest('user/verify-purchase-by-username', this.state,
            (data) => {
                console.log(data)
            }
        )
    }

    handleChange = (e) => {
        this.setState({username: e.target.value},
            () => {console.log(this.state)}
        )
    }

    render() {
        return (
            <div style={{height: '100%', display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'space-around', padding: '20px'}}>
                <input placeholder='username' style={{fontSize: '30px', display: 'flex', margin: '20px auto', width: '500px'}} className="text-response" value={this.state.username} onChange={this.handleChange}/>

                <button onClick={this.enterInstagram} style={{margin: '0 auto', fontSize: '30px'}}>Enter!</button>
            </div>
        )
    }
}

export default EnterInstagram
