import React from 'react';
import {Line} from 'react-chartjs-2';

const make_dataset = (dataset, labels, color, label) => {
  return {
      label: label,
      fill: false,
      lineTension: 0.6,
      backgroundColor: color,
      // backgroundColor: 'rgba(75,192,192,0.4)',
      // backgroundColor: '#ffffff',
      borderColor: color,
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: color,
    //   pointBackgroundColor: '#fff',
      pointBackgroundColor: color,
      pointBorderWidth: 5,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      pointHoverBorderColor: 'rgba(220,220,220,1)',
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: dataset
    }
  }


export default (props) => {
    const data = {
      // labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
      datasets: [],
      gridLines: [{
          display: false
      }]
    };
    data.labels = props.labels ? props.labels : [...Array(props.data.stints.length).keys()]
    var labels = [...Array(props.data.stints.length).keys()]
    // data.datasets[0].data = props.data.stints
    // data.datasets[0].label = 'Stints'
    // data.datasets[1].data = props.data.avails
    // data.datasets[1].label = 'Availabilities'
    var datasets = [props.data.stints, props.data.avails]
    var data_labels = ['Stints', '"Availabilities"']
    var colors = ['#48C0C0', '#ff9b21']
    for (var i in colors) {
      console.log(i)
      data.datasets.push(make_dataset(datasets[i], labels, colors[i], data_labels[i]))
    }
    console.log(data.datasets)
    return (
      <div className="" 
        style={{
          width: '390px',       backgroundColor: 'rgba(75,192,192,0.4)',
          margin:'20px', 
          borderRadius: '20px', 
          padding: '20px', 
          backgroundColor: 'whitesmoke',
          animationDelay: `${0.1*props.idx}s`
        }} >
        <div>{props.title}</div>
        {/* <div style={{backgroundColor: 'white'}}> */}

        <Line data={
            // labels: 'yooo',
            data
        } 
            // options={{backgroundColor: 'rgba(0, 0, 0, 0.5)'}}
        />
        {/* // height={40} width={2} options={{ maintainAspectRatio: false }} */}
        {/* </div> */}
      </div>
    );
};
