import React, { Component } from "react"
import Loading from "../general/Loading"
import { makePostRequest } from "../api_calls"
import AddOptions from "../general/form_components/AddOptions"
import Navbar from "../components/Navbar";

class AddBusiness extends Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: false,
            business_name: '',
            code: '',
            instagram: '',
            location: '',
            paypal: '',
            call_time: '',
            rewards: {}
        }
    }

    addOption = (key, val) => {
        this.setState(
            {
                rewards: {
                    ...this.state.rewards,
                    [key]: val
                }
            },
            () => {console.log(this.state.rewards)}
        )
    }

    removeOption = (key) => {
        var rewards = this.state.rewards
        delete rewards[key]
        this.setState({
            rewards
        })
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        },
            () => {
                console.log(this.state)
            }
        )
    }

    handleSubmit = () => {
        console.log('submitting')
        makePostRequest('admin/add-business', this.state,
            (response) => {
                console.log(response)
                console.log('done')
                this.props.closeSlideUp()
            }
        )
    }

    render() {
        var isValid = this.state.business_name && this.state.instagram
        console.log(isValid)
        return (
            <>
            <Navbar back={true} />
            <div className="body">
                <div className="panel">
                    <div className="medium">
                        Add a business!
                    </div>
                    <div className="form-container">
                        <div className="field-container">
                            <div className="field-title">
                                Business Name
                            </div>
                            <input id='business_name' value={this.state.business_name} onChange={this.handleChange} className="text-response"/>
                        </div>
                        <div className="field-container">
                            <div className="field-title">
                                Instagram
                            </div>
                            <input id='instagram' value={this.state.instagram} onChange={this.handleChange} className="text-response"/>
                        </div>
                        <div className="field-container">
                            <div className="field-title">
                                Instagram Password
                            </div>
                            <input id='instagram_password' type="password" value={this.state.instagram_password} onChange={this.handleChange} className="text-response"/>
                        </div>
                        <div className="field-container">
                            <div className="field-title">
                                Location
                            </div>
                            <input id='location' value={this.state.location} onChange={this.handleChange} className="text-response"/>
                        </div>
                        <AddOptions title='Reward scheme' key_name='Points' val_name='Reward' options={this.state.rewards} addOption={this.addOption} removeOption={this.removeOption} />

                        {/* <div className="field-container">
                            <div className="field-title">
                                Paypal
                            </div>
                            <input id='paypal' value={this.state.paypal} onChange={this.handleChange} className="text-response"/>
                        </div>
                        <div className="field-container">
                            <div className="field-title">
                                Call time
                            </div>
                            <input id='call_time' value={this.state.call_time} onChange={this.handleChange} className="text-response"/>
                        </div> */}
                        <button className="btn" disabled={!isValid} onClick={this.handleSubmit} style={{opacity: isValid ? 1 : 0.2}}>
                            {
                                this.state.loading ?
                                <Loading /> :
                                "Submit"
                            }
                        </button>
                    </div>
                    {/* <StripeProvider apiKey="pk_test_98UmCoevgIbIlIhQ23DgfoPq00nJOgxucq">
                        <div className="example">
                        <h1>React Stripe Elements Example</h1>
                        <Elements>
                            <CheckoutForm />
                        </Elements>
                        </div>
                    </StripeProvider> */}
                </div>
            </div>
            </>
        )
    }
}

export default AddBusiness
