import React, { Component } from "react"
import { makePostRequest } from "../../api_calls"

class EnterInstagram extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: ''
        }
    }

    enterInstagram = () => {
        makePostRequest('user/enterInstagram',
            (data) => {
                console.log(data)
            }
        )
    }

    handleChange = (e) => {
        this.setState({username: e.target.value})
    }

    render() {
        return (
            <div style={{height: '100%', display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'space-around'}}>
                <input style={{fontSize: '30px', padding: '10px', boxSizing: 'border-box'}} className="text-response" value={this.state.username} onChange={this.handleChange}/>
                <button onClick={this.enterInstagram}>Click to generate</button>
            </div>
        )
    }
}

export default EnterInstagram
